                <HStack spacing={4}>
                    <Input type='text' placeholder='Origin' />
                    <Input type='text' placeholder='Destination' />
                    <ButtonGroup>
                        <Button colorScheme='pink' type='submit'>
                            Calculate Route
                        </Button>
